function initSliders(a){var b=document.createElement("div");b.innerHTML='<div id="sliders" style=" position: relative;z-index: 1000;top : 10em;margin-left: auto;margin-right: auto;width: 70%;padding: 10px;background-color: whitesmoke;border-style: outset;border-width: 2px;"><h2>'+a+'</h2>Red: <div id="red"></div><br>Green: <div id="green"></div><br>Blue: <div id="blue"></div></div>';document.getElementById("alertWrapper").appendChild(b);var c=document.styleSheets;var d;for(var e=0;e<c.length;e++){if(/.*?bootstrap.min.css/.test(c[e].href)){d=c[e];break}}var f=d.cssRules;var g;var h=new RegExp(a);for(var e=0;e<f.length;e++){if(h.test(f[e].selectorText)){g=f[e];break}}jQuery("#sliders div").slider({min:0,max:255,slide:function(){var a=jQuery("#red").slider("value");var b=jQuery("#green").slider("value");var c=jQuery("#blue").slider("value");g.style.backgroundColor="rgb("+a+","+b+","+c+")";g.style.borderColor="rgb("+(a-29)+","+(b-5)+","+(c-6)+")";g.style.color="rgb("+(a-159)+","+(b-102)+","+(c-74)+")"}});var i=g.style.backgroundColor.match(/\d{1,3}/g);jQuery("#red").slider("value",i[0]);jQuery("#green").slider("value",i[1]);jQuery("#blue").slider("value",i[2]);return g}
